catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue === '') {
        g_form.setValue('price', '');
        g_form.setValue('total', '');
        return;
    }

    // Use the correct API Name of your Script Include
    var ga = new GlideAjax('x_656484_dinesmart.DineSmartOrderUtils');
    ga.addParam('sysparm_name', 'getMenuItemPrice');
    ga.addParam('sysparm_item_id', newValue);
    ga.getXML(updatePrice);

    function updatePrice(response) {
        var answer = response.responseXML.documentElement.getAttribute("answer");
        var price = parseFloat(answer) || 0;

        // Make sure your variable names are 'price', 'quantity', and 'total'
        g_form.setValue('price', price.toFixed(2));
        g_form.setReadOnly('price', true);

        var quantity = parseInt(g_form.getValue('quantity'), 10) || 1;
        var total = price * quantity;
        g_form.setValue('total', total.toFixed(2));
        g_form.setReadOnly('total', true);
		g_form.setValue('final_order_total', total.toFixed(2));
    }
}
