sys_variable_value.value:
(function(inputs, outputs) {
  // Add a small pause to ensure all records are saved
  gs.sleep(500); // Pauses for half a second

  // Get the dining order record directly from the input
  var diningOrderGR = new GlideRecord('x_656484_dinesmart_dining_orders');
  if (!diningOrderGR.get(inputs.dining_order)) {
    return;
  }

  var total = 0;
  var summary = [];

  // Find all related order items
  var orderItemsGR = new GlideRecord('x_656484_dinesmart_order_items');
  orderItemsGR.addQuery('order', inputs.dining_order);
  orderItemsGR.query();

  // Loop through them
  while (orderItemsGR.next()) {
    var price = parseFloat(orderItemsGR.price.toString());
    var quantity = parseInt(orderItemsGR.quantity.toString());
    total += price * quantity;
    summary.push(orderItemsGR.quantity + 'x ' + orderItemsGR.menu_item.getDisplayValue());
  }

  // Set the Action's Output Variables
  outputs.total_price = total;
  outputs.order_summary = summary.join(', ');

})(inputs, outputs);

