sys_script.script:
(function executeRule(current, previous /*null when async*/) {

    // Only run this rule for our specific catalog item
    if (current.cat_item.getDisplayValue() != 'Place a Dining Order') {
        return;
    }

    // 1. Create the main Dining Order record and link it to this Requested Item
    var diningOrderGR = new GlideRecord('x_656484_dinesmart_dining_orders');
    diningOrderGR.initialize();
    diningOrderGR.student = current.variables.requested_for;
    diningOrderGR.requested_item = current.sys_id;
    diningOrderGR.order_time = new GlideDateTime(); // ADD THIS LINE
    diningOrderGR.student_email = current.requested_for.email; // ADD THIS LINE
	diningOrderGR.order_status = 'order_received';
    var diningOrderSysId = diningOrderGR.insert();

    // 2. Get the MRVS data and create the Order Item records
    var mrvsData = JSON.parse(current.variables.your_order_items);
    for (var i = 0; i < mrvsData.length; i++) {
        var item = mrvsData[i];
        var orderItemsGR = new GlideRecord('x_656484_dinesmart_order_items');
        orderItemsGR.initialize();
        orderItemsGR.order = diningOrderSysId;
        orderItemsGR.menu_item = item.menu_item;
        orderItemsGR.quantity = item.quantity;
        orderItemsGR.price = item.price; 
        orderItemsGR.insert();
    }

    // 3. Now, do the final calculation
    var total = 0;
    var summary = [];

    var allOrderItemsGR = new GlideRecord('x_656484_dinesmart_order_items');
    allOrderItemsGR.addQuery('order', diningOrderSysId);
    allOrderItemsGR.query();

    while (allOrderItemsGR.next()) {
        var price = parseFloat(allOrderItemsGR.getValue('price')) || 0;
        var quantity = parseInt(allOrderItemsGR.getValue('quantity'), 10) || 0;
        total += price * quantity;
        summary.push(allOrderItemsGR.quantity + 'x ' + allOrderItemsGR.menu_item.getDisplayValue());
    }

    // 4. Update the records with the final totals
    diningOrderGR.get(diningOrderSysId);
    diningOrderGR.total_price = total.toFixed(2);
    diningOrderGR.order_summary = summary.join(', ');
    diningOrderGR.update();

    current.price = total.toFixed(2);
    current.special_instructions = summary.join(', ');
    current.update();

})(current, previous);
