sys_script.script:
(function executeRule(current, previous /*null when async*/) {

    gs.info("APP_LOG_DINESMART: Business Rule started for Order Item: " + current.menu_item.getDisplayValue());
    gs.info("APP_LOG_DINESMART: Trying to find parent Dining Order using ID: " + current.order);

    // Find the parent Dining Order record
    var diningOrderGR = new GlideRecord('x_656484_dinesmart_dining_orders');
    if (!diningOrderGR.get(current.order)) {
        gs.info("APP_LOG_DINESMART: ERROR - Could not find parent Dining Order.");
        return;
    }
    gs.info("APP_LOG_DINESMART: Found parent Dining Order: " + diningOrderGR.getDisplayValue());

    var total = 0;
    var summary = [];

    // Find all Order Items belonging to the same parent Dining Order
    var orderItemsGR = new GlideRecord('x_656484_dinesmart_order_items');
    orderItemsGR.addQuery('order', current.order);
    orderItemsGR.query();
    gs.info("APP_LOG_DINESMART: Query for Order Items found " + orderItemsGR.getRowCount() + " rows.");

    // Loop through them
    while (orderItemsGR.next()) {
        var price = parseFloat(orderItemsGR.price.toString());
        var quantity = parseInt(orderItemsGR.quantity.toString());
        total += price * quantity;
        summary.push(orderItemsGR.quantity + 'x ' + orderItemsGR.menu_item.getDisplayValue());
    }

    gs.info("APP_LOG_DINESMART: Calculation complete. Final Total: " + total);

    // Update the parent Dining Order
    diningOrderGR.total_price = total;
    diningOrderGR.update();

    // Find and update the original Requested Item
    var requestedItemGR = new GlideRecord('sc_req_item');
    if (requestedItemGR.get(diningOrderGR.requested_item)) {
        requestedItemGR.price = total;
        requestedItemGR.short_description = summary.join(', ');
        requestedItemGR.update();
        gs.info("APP_LOG_DINESMART: Successfully updated parent records.");
    } else {
        gs.info("APP_LOG_DINESMART: ERROR - Could not find Requested Item to update.");
    }

})(current, previous);
