sys_script_include.script:
var DineSmartOrderUtils = Class.create();
DineSmartOrderUtils.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    getMenuItemPrice: function() {
        var itemId = this.getParameter('sysparm_item_id');
        gs.info("## DINE_SMART_DEBUG ##: Script Include is running. Searching for Menu Item ID: " + itemId);

        var gr = new GlideRecord('x_656484_dinesmart_menu_items');

        if (gr.get(itemId)) {
            gs.info("## DINE_SMART_DEBUG ##: Found item: " + gr.getDisplayValue() + ", Price: " + gr.getValue('price'));
            return gr.getValue('price');
        }

        gs.info("## DINE_SMART_DEBUG ##: Item not found.");
        return '0.00';
    },

    type: 'DineSmartOrderUtils'
});
